package com.healthengagements.home;import android.app.enterprise.license.EnterpriseLicenseManager;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.widget.Toast;import com.smargav.api.prefs.PreferencesUtil;import org.apache.commons.lang3.StringUtils;public class SamsungELMLicenseReceiver extends BroadcastReceiver {    void showToast(Context context, CharSequence msg) {        Toast.makeText(context, msg, Toast.LENGTH_SHORT).show();    }    @Override    public void onReceive(Context context, Intent intent) {        String action = intent.getAction();        if (!EnterpriseLicenseManager.ACTION_LICENSE_STATUS.equals(action)) {            return;        }        String result = intent                .getStringExtra(EnterpriseLicenseManager.EXTRA_LICENSE_STATUS);        PreferencesUtil.putBoolean(context, Constants.LICENSE_ACCEPTED,                StringUtils.equalsIgnoreCase("success", result));    }}